import React, { useState, useEffect } from 'react';
import axios from 'axios';
import io from 'socket.io-client';
import './AdminPanel.css';

const token = localStorage.getItem('token');
const socket = io('http://localhost:5000', { auth: { token } });

const AdminPanel = () => {
    const [players, setPlayers] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetchPlayers();
    }, []);

    const fetchPlayers = async () => {
        try {
            const res = await axios.get('http://localhost:5000/api/players');
            setPlayers(res.data);
            setLoading(false);
        } catch (err) {
            console.error("Error fetching players", err);
        }
    };

    const startAuction = (playerId) => {
        // Send signal to server to start live auction for this player
        socket.emit("admin_start_auction", playerId);
        alert("Auction started! Check the Live Dashboard.");
    };

    if (loading) return <div>Loading Players...</div>;

    return (
        <div className="admin-container">
            <h2>Admin Control Panel</h2>
            <p>Select a player to begin their live auction.</p>
            
            <table className="player-list-table">
                <thead>
                    <tr>
                        <th>Player Name</th>
                        <th>Role</th>
                        <th>Base Price</th>
                        <th>Status</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {players.map(player => (
                        <tr key={player._id}>
                            <td>{player.name}</td>
                            <td>{player.role}</td>
                            <td>â‚¹{player.basePrice.toLocaleString()}</td>
                            <td>
                                <span className={`status-badge status-${player.status.toLowerCase()}`}>
                                    {player.status}
                                </span>
                            </td>
                            <td>
                                {player.status === 'Available' ? (
                                    <button 
                                        className="start-btn"
                                        onClick={() => startAuction(player._id)}
                                    >
                                        Start Auction
                                    </button>
                                ) : (
                                    <span>N/A</span>
                                )}
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default AdminPanel;
