import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './OwnerDashboard.css';

const OwnerDashboard = () => {
    const [team, setTeam] = useState(null);
    const [loading, setLoading] = useState(true);

    // Hardcoded for demonstration: In reality, get this from localStorage after login
    const ownerId = "INSERT_YOUR_OWNER_USER_ID_HERE"; 

    useEffect(() => {
        fetchTeamData();
    }, []);

    const fetchTeamData = async () => {
        try {
            // We'll assume an endpoint that gets team by owner
            const res = await axios.get(`http://localhost:5000/api/teams/owner/${ownerId}`);
            setTeam(res.data);
            setLoading(false);
        } catch (err) {
            console.error("Error fetching team", err);
            setLoading(false);
        }
    };

    if (loading) return <div>Loading Squad...</div>;
    if (!team) return <div className="owner-container"><h2>No Team registered for this owner.</h2></div>;

    return (
        <div className="owner-container">
            <h1>Team: {team.teamName}</h1>
            
            <div className="owner-stats">
                <div className="stat-card">
                    <h3>Total Budget</h3>
                    <p>₹{team.budget.toLocaleString()}</p>
                </div>
                <div className="stat-card" style={{borderColor: '#e67e22'}}>
                    <h3>Remaining Budget</h3>
                    <p>₹{team.remainingBudget.toLocaleString()}</p>
                </div>
            </div>

            <h2>Your Squad ({team.players.length})</h2>
            <div className="squad-grid">
                {team.players.map(player => (
                    <div key={player._id} className="player-mini-card">
                        <h4>{player.name}</h4>
                        <span>{player.role}</span>
                        <strong style={{color: '#27ae60'}}>₹{player.currentPrice.toLocaleString()}</strong>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default OwnerDashboard;
