import React, { useState, useEffect } from 'react';
import axios from 'axios';
import io from 'socket.io-client';
import './AdminPanel.css';

const token = localStorage.getItem('token');
const socket = io('http://localhost:5000', { auth: { token } });

const AdminPanel = () => {

    const [players, setPlayers] = useState([]);
    const [events, setEvents] = useState([]);
    const [newEventName, setNewEventName] = useState('');
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetchPlayers();
        fetchEvents();
    }, []);

    const fetchPlayers = async () => {
        try {
            const res = await axios.get('http://localhost:5000/api/admin/players', {
                headers: { Authorization: `Bearer ${token}` }
            });
            setPlayers(res.data);
            setLoading(false);
        } catch (err) {
            console.error("Error fetching players", err);
        }
    };

    const fetchEvents = async () => {
        try {
            const res = await axios.get('http://localhost:5000/api/events', {
                headers: { Authorization: `Bearer ${token}` }
            });
            setEvents(res.data);
        } catch (err) {
            console.error("Error fetching events", err);
        }
    };

    const createEvent = async () => {
        if (!newEventName) return;
        try {
            const res = await axios.post('http://localhost:5000/api/events', { name: newEventName }, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setEvents([res.data, ...events]);
            setNewEventName('');
        } catch (err) {
            console.error("Error creating event", err);
        }
    };

    const activateEvent = async (eventId) => {
        try {
            const res = await axios.post(`http://localhost:5000/api/events/${eventId}/activate`, {}, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setEvents(events.map(e => e._id === res.data._id ? res.data : { ...e, isActive: false }));
        } catch (err) {
            console.error("Error activating event", err);
        }
    };

    const startAuction = (playerId) => {
        socket.emit("admin_start_auction", playerId);
        alert("Auction started! Check the Live Dashboard.");
    };

    const approvePlayer = async (playerId) => {
        try {
            const res = await axios.post(`http://localhost:5000/api/admin/players/${playerId}/approve`, {}, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setPlayers(players.map(p => p._id === playerId ? res.data : p));
        } catch (err) {
            console.error("Error approving player", err);
        }
    };

    if (loading) return <div>Loading Players...</div>;

    return (
        <div className="admin-container">
            <h2>Admin Control Panel</h2>
            <p>Manage events, verify players, and control live auctions.</p>

            <div className="event-section">
                <h3>Events</h3>
                <div className="event-form">
                    <input
                        type="text"
                        placeholder="Event name e.g. IPL 2026"
                        value={newEventName}
                        onChange={e => setNewEventName(e.target.value)}
                    />
                    <button onClick={createEvent}>Create Event</button>
                </div>
                <ul className="event-list">
                    {events.map(event => (
                        <li key={event._id}>
                            <span>{event.name}</span>
                            {event.isActive ? (
                                <span className="active-badge">Active</span>
                            ) : (
                                <button onClick={() => activateEvent(event._id)}>Set Active</button>
                            )}
                        </li>
                    ))}
                </ul>
            </div>
            
            <table className="player-list-table">
                <thead>
                    <tr>
                        <th>Player Name</th>
                        <th>Role</th>
                        <th>Base Price</th>
                        <th>Status</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {players.map(player => (
                        <tr key={player._id}>
                            <td>{player.name}</td>
                            <td>{player.role}</td>
                            <td>â‚¹{player.basePrice.toLocaleString()}</td>
                            <td>
                                <span className={`status-badge status-${player.status.toLowerCase()}`}>
                                    {player.status}
                                </span>
                            </td>
                            <td>
                                {player.status === 'Pending' && (
                                    <button
                                        className="approve-btn"
                                        onClick={() => approvePlayer(player._id)}
                                    >
                                        Approve
                                    </button>
                                )}
                                {player.status === 'Approved' && (
                                    <button
                                        className="start-btn"
                                        onClick={() => startAuction(player._id)}
                                    >
                                        Start Auction
                                    </button>
                                )}
                                {(player.status === 'Sold' || player.status === 'Unsold') && (
                                    <span>N/A</span>
                                )}
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default AdminPanel;
