import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import AuctionDashboard from './components/AuctionDashboard/AuctionDashboard';
import AdminPanel from './components/AdminPanel/AdminPanel';
import PlayerRegistration from './components/PlayerRegistration/PlayerRegistration';
import OwnerDashboard from './components/OwnerDashboard/OwnerDashboard';
import Login from './components/Login/Login';
import UserRegistration from './components/UserRegistration/UserRegistration';
import Leaderboard from './components/Leaderboard/Leaderboard';
import './App.css';

function App() {
  const userString = localStorage.getItem('user');
  const user = userString ? JSON.parse(userString) : null;

  const logout = () => {
      localStorage.clear();
      window.location.href = '/login';
  };

  return (
    <Router>
      <div className="App">
        <nav style={{ padding: '15px', background: '#1a2a6c', color: 'white', display: 'flex', gap: '20px', alignItems: 'center' }}>
          <Link to="/" style={{ color: 'white', textDecoration: 'none', fontWeight: 'bold' }}>Live Auction</Link>
          <Link to="/leaderboard" style={{ color: 'white', textDecoration: 'none' }}>Leaderboard</Link>

          {!user ? (
            <>
              <Link to="/login" style={{ color: 'white', textDecoration: 'none', marginLeft: 'auto' }}>Login</Link>
              <Link to="/signup" style={{ color: 'white', textDecoration: 'none' }}>Register</Link>
            </>
          ) : (
            <>
              {user.role === 'Admin' && <Link to="/admin" style={{ color: 'white', textDecoration: 'none' }}>Admin Panel</Link>}
              {user.role === 'Owner' && <Link to="/owner-squad" style={{ color: 'white', textDecoration: 'none' }}>My Squad</Link>}
              <button onClick={logout} style={{background: '#e74c3c', border: 'none', color: 'white', padding: '5px 10px', borderRadius: '4px', cursor: 'pointer', marginLeft: 'auto'}}>Logout</button>
            </>
          )}
        </nav>

        <Routes>
          <Route path="/" element={<AuctionDashboard />} />
          <Route path="/login" element={<Login />} />
          <Route path="/signup" element={<UserRegistration />} />
          <Route path="/leaderboard" element={<Leaderboard />} />
          <Route path="/register-player" element={<PlayerRegistration />} />
          <Route path="/owner-squad" element={<OwnerDashboard />} />
          <Route path="/admin" element={<AdminPanel />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
